import React, {Component} from 'react';
import axios from 'axios';

class UserAdd extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userlist_firstname: '',
            userlist_lastname: '',
            userlist_age: 0,
            userlist_gender: '',
            userlist_password: '',
            userlist_repeatpassword: ''
        };
    }

    onChangeUserlistFirstName = e => {
        this.setState({userlist_firstname: e.target.value});
    }

    onChangeUserlistLastName = e => {
        this.setState({userlist_lastname: e.target.value});
    }

    onChangeUserlistAge = e => {
        this.setState({userlist_age: e.target.value});
    }

    onChangeUserlistGender = e => {
        this.setState({userlist_gender: e.target.value});
    }

    onChangeUserlistPassword = e => {
        this.setState({userlist_password: e.target.value});
    }

    onChangeUserlistRepeatPassword = e => {
        this.setState({userlist_repeatpassword: e.target.value});
    }

    onSubmit = e => {
        e.preventDefalut();

        const newUserlist = {
            userlist_firstname: this.state.userlist_firstname,
            userlist_lastname: this.state.userlist_lastname,
            userlist_age: this.state.userlist_age,
            userlist_gender: this.state.userlist_gender,
            userlist_password: this.state.userlist_password,
            userlist_repeatpassword: this.state.userlist_repeatpassword
        };

        axios.post('http://localhost:4000/userlistdb/useradd', newUserlist)
            .then(res => console.log(res.data));

        this.setState({
            userlist_firstname: '',
            userlist_lastname: '',
            userlist_age: 0,
            userlist_gender: '',
            userlist_password: '',
            userlist_repeatpassword: ''
        });
    }

    render () {
      return (
        <div>
            <h3>Add User</h3>
            <form onSubmit={this.onSubmit}>
              <div>
                <label>First Name：</label>
                <input 
                    type="text"
                    value={this.state.userlist_firstname}
                    onChange={this.onChangeUserlistFirstName}
                />
              </div>
              <div>
                <label>Last Name：</label>
                <input 
                    type="text"
                    value={this.state.userlist_lastname}
                    onChange={this.onChangeUserlistLastName}
                />
              </div>
              <div>
                <label>Age：</label>
                <input 
                    type="number"
                    value={this.state.userlist_age}
                    onChange={this.onChangeUserlistAge}
                />
              </div>
              <div>
                <div className="form-check form-check-inline">
                    <input
                        type="radio"
                        value="Male"
                        checked={this.state.userlist_gender==='Male'}
                        onChange={this.onChangeUserlistGender}
                    />
                    <label className="form-check-label">Male</label>
                </div>
                <div className="form-check form-check-inline">
                    <input  
                        type="radio"
                        value="Female"
                        checked={this.state.userlist_gender==='Female'}
                        onChange={this.onChangeUserlistGender}
                    />
                    <label className="form-check-label">Female</label>
                </div>
              </div>
              <div>
                <label>Password：</label>
                <input 
                    type="number"
                    value={this.state.userlist_password}
                    onChange={this.onChangeUserlistPassword}
                />
              </div>
              <div>
                <label>Repeat password：</label>
                <input 
                    type="number"
                    value={this.state.userlist_repeatpassword}
                    onChange={this.onChangeUserlistRepeatPassword}
                />
              </div>
              <div>
                  <input type="submit" value="submit"/>
            </div>
            </form>       
        </div>
      );
    }
  }

  export default UserAdd;
