import React, {Component} from 'react';
import axios from 'axios';

class UserAdd extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userlist_firstname: '',
            userlist_lastname: '',
            userlist_age: 0,
            userlist_gender: '',
            userlist_password: '',
            userlist_repeatpassword: ''
        };
    }

    onChangeUserlistFirstName = e => {
        this.setState({userlist_firstname: e.target.value});
    }

    onChangeUserlistLastName = e => {
        this.setState({userlist_lastname: e.target.value});
    }

    onChangeUserlistAge = e => {
        this.setState({userlist_age: e.target.value});
    }

    onChangeUserlistGender = e => {
        this.setState({userlist_gender: e.target.value});
    }

    onChangeUserlistPassword = e => {
        this.setState({userlist_password: e.target.value});
    }

    onChangeUserlistRepeatPassword = e => {
        this.setState({userlist_repeatpassword: e.target.value});
    }

    onSubmit = e => {
        e.preventDefalut();

        const newUserlist = {
            userlist_firstname: this.state.userlist_firstname,
            userlist_lastname: this.state.userlist_lastname,
            userlist_age: this.state.userlist_age,
            userlist_gender: this.state.userlist_gender,
            userlist_password: this.state.userlist_password,
            userlist_repeatpassword: this.state.userlist_repeatpassword
        };

        axios.post('http://localhost:4000/userlistdb/useradd', newUserlist)
            .then(res => console.log(res.data));

        this.setState({
            userlist_firstname: '',
            userlist_lastname: '',
            userlist_age: 0,
            userlist_gender: '',
            userlist_password: '',
            userlist_repeatpassword: ''
        });
    }

    render () {
      return (
        <div>
          <header>
            <h1>Add User</h1>
          </header>

          <main>
            <form>
              <label>First Name：</label>
              <input type="text"/>
              <br/>
              <label>Last Name：</label>
              <input type="text"/>
              <br/>
              <label>Age：</label>
              <input type="number"/>
              <br/>
              <label>Gender：</label>
              <select>
                <option value="">Option</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="unknown">Unknown</option>
              </select>
              <br/>
              <label>Password：</label>
              <input type="number"/>
              <br/>
              <label>Repeat password：</label>
              <input type="number"/>
              <br/>
              <input type="submit" value="submit"/>
            </form>
          </main>
        </div>
      );
    }
  }

  export default UserAdd;
