import React ,{Component} from 'react';
import { connect } from "react-redux";
import {addUser} from "../actions";

class Add extends Component {
    constructor(props) {
        super(props);
        this.state = {
            first_name: '',
            last_name: '',
            age: '',
            gender: '',
            password: '',
            repeatpassword: ''
        };
    }

    onChangeUserlistFirstName = e => {
        this.setState({first_name: e.target.value});
    }

    onChangeUserlistLastName = e => {
        this.setState({last_name: e.target.value});
    }

    onChangeUserlistAge = e => {
        this.setState({age: e.target.value});
    }

    onChangeUserlistGender = e => {
        this.setState({gender: e.target.value});
    }

    onChangeUserlistPassword = e => {
        this.setState({password: e.target.value});
    }

    onChangeUserlistRepeatPassword = e => {
        this.setState({repeatpassword: e.target.value});
    }

    onSubmit = e => {
        e.preventDefalut();

        let newUser = {
            first_name: this.state.first_name,
            last_name: this.state.lastname,
            age: this.state.age,
            gender: this.state.gender,
            password: this.state.password,
            repeatpassword: this.state.repeatpassword
        };

        this.props.dispatch(addUser(newUser));
    }

    render () {
      return (
        <div>
            <h3>Add User</h3>
            <form onSubmit={this.onSubmit}>
              <div>
                <label>First Name：</label>
                <input 
                    type="text"
                    value={this.state.userlist_firstname}
                    onChange={this.onChangeUserlistFirstName}
                />
              </div>
              <div>
                <label>Last Name：</label>
                <input 
                    type="text"
                    value={this.state.userlist_lastname}
                    onChange={this.onChangeUserlistLastName}
                />
              </div>
              <div>
                <label>Age：</label>
                <input 
                    type="number"
                    value={this.state.userlist_age}
                    onChange={this.onChangeUserlistAge}
                />
              </div>
              <div>
                <div className="form-check form-check-inline">
                    <input
                        type="radio"
                        value="Male"
                        checked={this.state.userlist_gender==='Male'}
                        onChange={this.onChangeUserlistGender}
                    />
                    <label className="form-check-label">Male</label>
                </div>
                <div className="form-check form-check-inline">
                    <input  
                        type="radio"
                        value="Female"
                        checked={this.state.userlist_gender==='Female'}
                        onChange={this.onChangeUserlistGender}
                    />
                    <label className="form-check-label">Female</label>
                </div>
              </div>
              <div>
                <label>Password：</label>
                <input 
                    type="number"
                    value={this.state.userlist_password}
                    onChange={this.onChangeUserlistPassword}
                />
              </div>
              <div>
                <label>Repeat password：</label>
                <input 
                    type="number"
                    value={this.state.userlist_repeatpassword}
                    onChange={this.onChangeUserlistRepeatPassword}
                />
              </div>
              <div>
                  <input type="submit" value="submit"/>
            </div>
            </form>       
        </div>
      );
    }
  }

const mapStateToProps = state => (
    {
        err: state.list.err
    }
)

const mapDispatchToProps = dispatch => (
    {
        addUser: user => {
            dispatch(addUser(user));
        }
    }
)

export default connect(mapStateToProps, mapDispatchToProps)(Add);
