import axios from "axios";

const getUserRequest = () => {
  return {
    type: "GET_USER_REQUEST"
  };
};

const getUserSuccess = (data) => {
  return {
    type: "GET_USER_SUCCESS",
    data: data
  };
};

const getUserFail = (err) => {
  return {
    type: "GET_USER_FAIL",
    err: err
  };
};

export const getUser = () => {
    return (dispatch) => {
      dispatch(getUserRequest());
      axios.get("http://localhost:4000/userlistdb")
        .then(res => {
          dispatch(getUserSuccess(res.data));
        })
        .catch(err => {
          dispatch(getUserFail(err));
        });
    };
  };

const createSuccess = (data) => {
    console.log(data)
    return {
        type : "CREATE_USER_SUCCESS",
        data : data
    };
}

const creaetFail = (err) => {
    return {
        type: "CREATE_USER_FAIL",
        err: err
      };    
}

export function createUser(newUser) {
    return (dispatch, getState) => {
        axios
        .post("http://localhost:4000/userlistdb/useradd", newUser)
        .then(res => {
          dispatch(createSuccess(res.data.user));
        })
        .catch(err => {
          dispatch(creaetFail(err));
        });
    }
}

export function deleteUser(id) {
    return (dispatch, getState) => {
        axios
        .post(`http://localhost:4000/userlistdb/delete/${id}`)
        .then(res => {
          dispatch(getUser());
        })
        .catch(err => {
          console.log(err);
        });        
    }
}

const getDetail = (userDetail) => {
    return {
        type : "GET_DETAIL_SUCCESS",
        userDetail : userDetail
    };
}

export function getUserById(id) {
    return (dispatch, getState) => {
        axios
        .get(`http://localhost:4000/userlistdb/${id}`)
        .then(res => {
            dispatch(getDetail(res));
        })
        .catch(err => {
            console.log(err);
        });
    }
}

const editSuccess = (data) => {
    return {
        type : "EDIT_SUCCESS",
        data : data
    };
}

export function editUser(user) {
    return (dispatch, getState) => {
        axios
        .post(`http://localhost:4000/userlistdb/useredit/${user._id}`, user)
        .then(res => {
            //dispatch(getUser());
            dispatch(editSuccess(res.data));
        })
        .catch(err => {
            console.log(err);
        });
    }
}
