import React from 'react';
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../actions";
import User from "./user.js";


class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          users: [],
        };
    }

    componentDidMount() {
        this.props.getUserList();
        this.setState({ users: this.props.users });
        console.log("log below from react")
        console.log(this.props.users);
    }

    componentWillReceiveProps(nextProps) {
        this.setState({ users: nextProps.users });
    }

    onDeleteHandler = (userId) => {

        this.props.deleteUser(userId);

    }

    onEditHandler = (Id) => {
        this.props.getUserById(Id);
        this.props.history.push(`/edit/${Id}`);    
    }

    render(){
        return (  
            <div> 
                <h3>User List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>Edit</th>
                            <th>Delete</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Age</th>
                            <th>Gender</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.state.users.map((eachUser, idx) => {
                            return (<User user = {eachUser} key = {idx} onDeleteHandler = {this.onDeleteHandler} onEditHandler = {this.onEditHandler}/>);
                            })
                        }
                    </tbody>
                </table>               
            </div>

        ); 

    }
}

const mapStateToProps = state => {
    return {
      users: state.getUsers.data,
      detail : state.Detail.userDetail.data
    };
};

const mapDispatchToProps = dispatch => {
    return {
      getUserList: () => {
        dispatch(actions.getUser());
      },
      deleteUser : (userId) => {
        dispatch(actions.deleteUser(userId));    
      },
      getUserById : (Id) => {
        dispatch(actions.getUserById(Id));
      }

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
