import React from 'react';
import { connect } from "react-redux";
import {getList, deleteUser} from "../actions";
import 'bootstrap/dist/css/bootstrap.min.css';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            filterInfo: ""
        };
    }

    componentDidMount() {
        const { getList } = this.props;
        getList();
    }

    handleChange = e => {
        this.setState({ filterInfo: e.target.value });
    }

    handleData = (filterInfo) => {
        let tmpUsers = [...this.props.users];
        if (!!filterInfo) {
            filterInfo = filterInfo.toLowerCase();
            tmpUsers = tmpUsers.filter((user) => {
                return (user.first_name.toLowerCase().indexOf(filterInfo) !== -1) 
                    ||(user.last_name.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.age.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.gender.toLowerCase().indexOf(filterInfo) !== -1);
            });
        }
        return tmpUsers;
    }

    handleDelete = (record) => {
        const { history, deleteUser } = this.props;
        deleteUser(record._id);
    }

    handleClickAdd = () => {
        this.props.history.push("/add");
    };

    render(){
        return (  
            <div> 
                <div>
                <input class="form-control" id="myInput" type="text" placeholder="Search..."/>
                </div>
                <table className="table table-striped table-hover">
                    <thead className="thead-dark">
                        <tr>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Age</th>
                            <th>Gender</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {/* { this.state.users.map((eachUser, idx) => {
                            return (<User user = {eachUser} key = {idx} onDeleteHandler = {this.onDeleteHandler} onEditHandler = {this.onEditHandler}/>);
                            })
                        } */}
                    </tbody>
                </table>               
            </div>

        ); 

    }
}

//connect redux to component
const mapStateToProps = state => (
    {
        users: state.list.data,
        err: state.list.err
    }
);

const mapDispatchToProps = dispatch => (
    {
        getList: () => {
            dispatch(getList());
        },
        deleteUser: (id, message, history) => {
            dispatch(deleteUser(id, message, history));
        }
    }
);

export default connect(mapStateToProps, mapDispatchToProps)(Home);
