import React from 'react';
import { connect } from "react-redux";
import { getDetail, updateUser } from "../actions";

class UserEdit extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            first : "",
            last : "",
            age : "",
            gender : "",
            password : "",
            _id : ""
        }
    }


    componentWillReceiveProps(nextProps) {
        this.setState({            
            first : nextProps.detail.first,
            last : nextProps.detail.last, 
            age : nextProps.detail.age,
            gender : nextProps.detail.gender,
            password : nextProps.detail.password,
            _id : nextProps.detail._id 
        });
    }

    onChangeFirst = (e) => {
        this.setState({first : e.target.value})    
    } 
    onChangeLast = (e) => {
        this.setState({last : e.target.value})    
    } 
    onChangeSex = (e) => {
        this.setState({sex : e.target.value})    
    } 
    onChangeAge = (e) => {
        this.setState({age : e.target.value})    
    } 
    onChangePassword = (e) => {
        this.setState({password : e.target.value})    
    } 

    onSubmit = e => {
        e.preventDefault();

        let updateUser = {
            first: this.state.first,
            last : this.state.last,
            age : this.state.age,
            gender : this.state.gender,
            password : this.state.password,
            _id : this.state._id
        };

        //this.props.dispatch(actions.editUser(updateUser));
        this.props.history.push("/");  

    };

    render(){
        const {first, last, age, gender, password} = this.state;
        return (
            <div style = {{marginTop : 5}}>
                <h1>Edit User</h1>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group" style={{width : "200px"}}> 
                        <label>First Name : </label>
                        <input  type="text"
                                className="form-control"
                                value={first}
                                onChange={this.onChangeFirst}
                                placeholder = "First Name"
                                />
                    </div>
                    <div className="form-group" style={{width : "200px"}}> 
                        <label>Last Name : </label>
                        <input  type="text"
                                className="form-control"
                                value={last}
                                onChange={this.onChangeLast}
                                placeholder = "Last Name"
                                />
                    </div>
                    <div className="form-group" style={{width : "200px"}}> 
                        <label>Age : </label>
                        <input  type="text"
                                className="form-control"
                                value={age}
                                onChange={this.onChangeAge}
                                placeholder = "Age"
                                />
                    </div>
                    <div className="form-group" style={{width : "200px"}}> 
                        <label>Sex : </label>
                        <input  type="text"
                                className="form-control"
                                value={gender}
                                onChange={this.onChangeSex}
                                placeholder = "Gender"
                                />
                    </div>
                    <div className="form-group" style={{width : "200px"}}> 
                        <label>Password : </label>
                        <input  type="text"
                                className="form-control"
                                value={password}
                                onChange={this.onChangePassword}
                                placeholder = "Password"
                                />
                    </div>


                    <div className="form-group">

                        <button type="submit" className="btn btn-success" >
                        Save Changes
                        </button>     


                    </div>
                </form>
            </div>  
        )      
    }

};

const mapStateToProps = state => {
    return {
        users: state.getUsers.data,
        detail : state.Detail.userDetail.data
    };
  };

export default connect(mapStateToProps)(UserEdit);
