import React from 'react';
import { connect } from "react-redux";
import {getList, deleteUser} from "../actions";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import Table from 'react-bootstrap/Table';

const User = ({user, key, handleDelete, handleEdit}) => (
    <tr key={key}>
        <td>{user.first_name}</td>
        <td>{user.last_name}</td>
        <td>{user.age}</td>
        <td>{user.gender}</td>
        <td>
        <button type="button" class="btn btn-outline-primary" id="edit-btn" onClick = {() => handleEdit(user.id)}>
            <i className="fas fa-edit" />
            Edit
        </button>
        </td>

        <td>
        <button type="button" class="btn btn-outline-secondary" id="delete-btn" onClick = {() => handleDelete(user.id)}>
            <i className="fas fa-trash-alt" />
            Delete
        </button>
        </td>
    </tr>
)

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: []
        };
    }

    componentDidMount() {
        this.props.getList();
        this.setState({ users: this.props.users });
    }

    handleChange = e => {
        this.setState({ filterInfo: e.target.value });
    }

    handleData = (filterInfo) => {
        let tmpUsers = [...this.props.users];
        if (!!filterInfo) {
            filterInfo = filterInfo.toLowerCase();
            tmpUsers = tmpUsers.filter((user) => {
                return (user.first_name.toLowerCase().indexOf(filterInfo) !== -1) 
                    ||(user.last_name.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.age.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.gender.toLowerCase().indexOf(filterInfo) !== -1);
            });
        }
        return tmpUsers;
    }

    handleDelete = (userId) => {
        this.props.deleteUser(userId);
    }

    handleEdit = (id) => {
        this.props.getUserById(id);
        this.props.history.push(`/edit/${id}`);    
    }

    handleClickAdd = () => {
        this.props.history.push("/add");
    };

    render(){
        const { err, getList } = this.props;
        const { filterInfo } = this.state;

        return (  
            <div> 
                <Table striped bordered hover >
                    <thead>
                        <tr>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Sex</th>
                            <th>Age</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        { 
                            this.state.users.map((eachUser, idx) => {
                                return (<User 
                                    user = {eachUser} 
                                    key = {idx} 
                                    handleDelete={this.handleDelete} 
                                    handleEdit={this.handleEdit}
                                />);
                            })
                        }
                    </tbody>
                </Table>               
            </div>

        ); 

    }
}

//connect redux to component
const mapStateToProps = state => (
    {
        users: state.list.data,
        err: state.list.err
    }
);

const mapDispatchToProps = dispatch => (
    {
        getList: () => {
            dispatch(getList());
        },
        deleteUser: (id, message, history) => {
            dispatch(deleteUser(id, message, history));
        }
    }
);

export default connect(mapStateToProps, mapDispatchToProps)(Home);
