const initialState = {
    data : [],
    err : null,
    isLoading : false
};
const getUsers = (state = initialState, action) => {
    switch(action.type) {
        case "GET_USER_REQUEST":
          return {
            ...state,
            isLoading: true
          };
        case "GET_USER_SUCCESS":
          return {
            isLoading: false,
            data: action.data,
            err: null
          };
        case "GET_USER_FAIL":
          return {
            ...state,
            isLoading: false,
            err: action.err
          };

        case "CREATE_USER_SUCCESS" : 
          state.data.push(action.data);
          const newdata = state.data;
          return {
              data : newdata,
              isLoading: false,
              err: null
          };

        case "CREATE_USER_FAIL":
          return {
            ...state,
            isLoading: false,
            err: action.err
        };

        case"EDIT_SUCCESS":
          const newArray = state.data.map(x => {
              if(x._id === action.data._id){
                  return action.data;
              }
              else{
                  return x;
              }
          })
          return {
            data : newArray,
            isLoading : false,
            err : null
          };

        default:
          return state; 
      }
}

export default getUsers;
