import React from 'react';
import { connect } from "react-redux";
import {getList, deleteUser} from "../actions";
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import Table from 'react-bootstrap/Table';


const Userlist = props => (
    <tr>
        <td>{props.user.first_name}</td>
        <td>{props.user.last_name}</td>
        <td>{props.user.age}</td>
        <td>{props.user.gender}</td>
        <td>
            <Link to={"/edit/"+props.user.id}>Edit</Link>
        </td>
    </tr>
)

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            filterInfo: ""
        };
    }

    componentDidMount() {
        const { getList } = this.props;
        getList();
    }

    handleChange = e => {
        this.setState({ filterInfo: e.target.value });
    }

    handleData = (filterInfo) => {
        let tmpUsers = [...this.props.users];
        if (!!filterInfo) {
            filterInfo = filterInfo.toLowerCase();
            tmpUsers = tmpUsers.filter((user) => {
                return (user.first_name.toLowerCase().indexOf(filterInfo) !== -1) 
                    ||(user.last_name.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.age.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.gender.toLowerCase().indexOf(filterInfo) !== -1);
            });
        }
        return tmpUsers;
    }

    handleDelete = (record) => {
        const { deleteUser } = this.props;
        deleteUser(record.id);
    }

    handleClickAdd = () => {
        this.props.history.push("/add");
    };

    render(){
        const { err, getList } = this.props;
        const { filterInfo } = this.state;

        return (  
            <div> 
                <Table striped bordered hover >
                    <thead>
                        <tr>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Sex</th>
                            <th>Age</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        { 
                            this.state.userlistdb.map((eachUser, idx) => {
                            return (<Userlist user = {eachUser} key = {idx} onDeleteHandler = {this.onDeleteHandler} onEditHandler = {this.onEditHandler}/>);
                            })
                        }
                    </tbody>
                </Table>               
            </div>

        ); 

    }
}

//connect redux to component
const mapStateToProps = state => (
    {
        users: state.list.data,
        err: state.list.err
    }
);

const mapDispatchToProps = dispatch => (
    {
        getList: () => {
            dispatch(getList());
        },
        deleteUser: (id, message, history) => {
            dispatch(deleteUser(id, message, history));
        }
    }
);

export default connect(mapStateToProps, mapDispatchToProps)(Home);
