import React from 'react';
import { connect } from "react-redux";
import * as actions from "../actions";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import Table from 'react-bootstrap/Table';
import Pagination from 'react-bootstrap/Pagination'


const User = ({user, key, handleDelete, handleEdit}) => (
    <tr key={key}>
        <td>{user.first_name}</td>
        <td>{user.last_name}</td>
        <td>{user.age}</td>
        <td>{user.gender}</td>
        <td>
        <button type="button" class="btn btn-outline-primary" id="edit-btn" onClick = {() => handleEdit(user._id)}>
            <i className="fas fa-edit" />
            Edit
        </button>
        </td>

        <td>
        <button type="button" class="btn btn-outline-secondary" id="delete-btn" onClick = {() => handleDelete(user._id)}>
            <i className="fas fa-trash-alt" />
            Delete
        </button>
        </td>
    </tr>
)

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: []
        };
    }

    componentDidMount() {
        this.props.getList();
        this.setState({ users: this.props.users });
    }

    componentWillReceiveProps(nextProps) {
        this.setState({users: nextProps.users});
    }

    handleChange = e => {
        this.setState({ users: e.target.value });
    }

    handleData = (filterInfo) => {
        let tmpUsers = [...this.props.users];
        if (filterInfo) {
            filterInfo = filterInfo.toLowerCase();
            tmpUsers = tmpUsers.filter((user) => {
                return (user.first_name.toLowerCase().indexOf(filterInfo) !== -1) 
                    ||(user.last_name.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.age.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.gender.toLowerCase().indexOf(filterInfo) !== -1);
            });
        }
        return tmpUsers;
    }

    handleDelete = (userId) => {
        this.props.deleteUser(userId);
    }

    handleEdit = (id) => {
        this.props.getDetail(id);
        this.props.history.push(`/edit/${id}`);    
    }

    handleClickAdd = () => {
        this.props.history.push("/add");
    };

    render(){
        // const { err, getList } = this.props;
        // const { filterInfo } = this.state;

        return (  
            <div> 
                <div>
                    <input type="text" className="input" onChange={this.handleChange} placeholder="Search..." />
                </div>
                <Table striped bordered hover >
                    <thead>
                        <tr>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Sex</th>
                            <th>Age</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        { 
                            this.state.users.map((eachUser, idx) => {
                                return (<User 
                                    user = {eachUser} 
                                    key = {idx} 
                                    handleDelete={this.handleDelete} 
                                    handleEdit={this.handleEdit}
                                />);
                            })
                        }
                    </tbody>
                </Table>
                <div>
                <Pagination>
                    <Pagination.First />
                    <Pagination.Prev />
                    <Pagination.Item>{1}</Pagination.Item>
                    <Pagination.Item>{2}</Pagination.Item>
                    <Pagination.Item>{3}</Pagination.Item>
                    <Pagination.Next />
                    <Pagination.Last />
                    </Pagination>
                </div>               
            </div>

        ); 

    }
}

//connect redux to component
const mapStateToProps = state => (
    {
        users: state.list.data,
        err: state.list.err
    }
);

const mapDispatchToProps = dispatch => (
    {
        getList: () => {
            dispatch(actions.getList());
        },
        deleteUser: (userId) => {
            dispatch(actions.deleteUser(userId));
        }
    }
);

export default connect(mapStateToProps, mapDispatchToProps)(Home);
