import React from 'react';
import { connect } from "react-redux";
import {getList, deleteUser} from "../actions";
import 'bootstrap/dist/css/bootstrap.min.css';
import BootstrapTable from 'react-bootstrap-table-next';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            filterInfo: ""
        };
    }

    componentDidMount() {
        const { getList } = this.props;
        getList();
    }

    handleChange = e => {
        this.setState({ filterInfo: e.target.value });
    }

    handleData = (filterInfo) => {
        let tmpUsers = [...this.props.users];
        if (!!filterInfo) {
            filterInfo = filterInfo.toLowerCase();
            tmpUsers = tmpUsers.filter((user) => {
                return (user.first_name.toLowerCase().indexOf(filterInfo) !== -1) 
                    ||(user.last_name.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.age.toLowerCase().indexOf(filterInfo) !== -1)
                    ||(user.gender.toLowerCase().indexOf(filterInfo) !== -1);
            });
        }
        return tmpUsers;
    }

    handleDelete = (record) => {
        const { deleteUser } = this.props;
        deleteUser(record.id);
    }

    handleClickAdd = () => {
        this.props.history.push("/add");
    };

    render(){
        const { err, getList } = this.props;
        const { filterInfo } = this.state;
        const columns = [
            {
                text: 'First Name',
                dataField: 'first_name',
                key: 'first_name',
                sort: true,
                sorter: (a, b) => {
                    if (a.first_name < b.first_name) return -1;
                    else if (a.first_name > b.first_name) return 1;
                    return 0;
                },
            },
            {
                title: 'Last Name',
                dataIndex: 'last_name',
                key: 'last_name',
                sort: true,
                sorter: (a, b) => {
                    if (a.last_name < b.last_name) return -1;
                    else if (a.last_name > b.last_name) return 1;
                    return 0;
                },
            },
            {
                title: 'Age',
                dataIndex: 'age',
                key: 'age',
                sorter: (a, b) => a.age - b.age,
            },
            {
                title: 'Gender',
                dataIndex: 'gender',
                key: 'gender',
            },


        ]

        return (  
            <div> 
                <div>
                <input value={filterInfo} placeholder="Search..." onChange={this.handleChange} />
                </div>
                <BootstrapTable className="table table-striped table-hover" 
                    columns={columns} 
                    data={this.handleData(filterInfo)}
                />              
            </div>

        ); 

    }
}

//connect redux to component
const mapStateToProps = state => (
    {
        users: state.list.data,
        err: state.list.err
    }
);

const mapDispatchToProps = dispatch => (
    {
        getList: () => {
            dispatch(getList());
        },
        deleteUser: (id, message, history) => {
            dispatch(deleteUser(id, message, history));
        }
    }
);

export default connect(mapStateToProps, mapDispatchToProps)(Home);
